mapper.sql:
/* 검색 */

	AND (
	    c.title LIKE CONCAT('%', #{requestDTO.search}, '%')
		OR c.description LIKE CONCAT('%', #{requestDTO.search}, '%')
	)

/* 필터: 국가 */

	AND co.id = #{requestDTO.filter.country}


/* 필터: 도시 */

	AND ct.id = #{requestDTO.filter.city}


/* 필터: 카테고리 (배열) */

	AND cg.id IN

		#{cat}



/* 필터: 테마 */

	AND EXISTS (
		SELECT	1
		FROM	card_theme t
		WHERE	t.card_id = c.id
				AND t.theme_id IN

				#{themeId}

	)


/* 필터: 스크랩 여부 */



		AND cs.card_id IS NOT NULL


		AND cs.card_id IS NULL

mapper.update:
UPDATE	my_cards
SET		memo = #{memo}
WHERE	id = #{cardId}

UPDATE	my_cards
SET 	main_image_id = #{myCardImageId}
		, main_image_type = #{mainImageType}
WHERE	id = #{myCardId}

mapper.select:
SELECT	COUNT(*)
FROM	my_cards mc
		INNER JOIN (

		) ec ON mc.card_id = ec.card_id
WHERE	mc.user_id = #{userId}
		AND mc.is_active = true
